<?php

namespace App\Authors;

class ParentingTest extends \TestCase
{
    public function testToXml()
    {
        $mock = $this->initMock(\App\Fetcher::class);
        $mock
        ->shouldReceive('get')->andReturn($this->xml())
        ->shouldReceive('cacheByUrls')
        ->shouldReceive('getCacheByUrls')->andReturn($this->bodies());

        $author = app(Parenting::class);
        $this->assertStringContainsString('有一段非常大的距離', $author->toXml());
    }

    protected function bodies()
    {
        $html = <<<HTML
{"data":{"article":{"content":"<h2><span style=\"color: #ff9900;\"><strong>\u5bb3\u6015\u4e92\u52d5\u7684\u5b69\u5b50<\/strong><\/span><\/h2>\r\n<p>\u8aee\u5546\u6642\u9593\u904e\u4e86\u597d\u4e00\u9663\u5b50\u4e86\uff0c\u537b\u4e0d\u898b\u5b69\u5b50\u4f86\u3002<\/p>\r\n<p>\u9023\u8001\u5e2b\u6709\u4e9b\u7d0d\u60b6\uff0c\u300c\u96e3\u4e0d\u6210\uff0c\u6587\u5349\u4eca\u5929\u8acb\u5047\uff0c\u5c0e\u5e2b\u6c92\u8f49\u9054\uff1f\u9084\u662f\u2026\u2026\u300d<\/p>\r\n<p>\u70ba\u4e86\u78ba\u8a8d\u5b69\u5b50\u662f\u5426\u5df2\u5f9e\u539f\u73ed\u6559\u5ba4\u96e2\u958b\uff0c\u524d\u4f86\u6664\u8ac7\u5ba4\uff0c\u65bc\u662f\uff0c\u9023\u8001\u5e2b\u64a5\u4e86\u901a\u96fb\u8a71\u5230\u56db\u5e74\u4e09\u73ed\u6559\u5ba4\uff1a\u300c\u85cd\u8001\u5e2b\uff0c\u60a8\u597d\uff0c\u8acb\u554f\u6587\u5349\u4eca\u5929\u6709\u8acb\u5047\u55ce\uff1f\u56e0\u70ba\u76ee\u524d\u6211\u9084\u6c92\u770b\u5230\u4eba\u3002\u300d<\/p>\r\n<p>\u96fb\u8a71\u90a3\u7aef\u50b3\u4f86\uff1a\u300c\u6587\u5349\u5df2\u7d93\u96e2\u958b\u6559\u5ba4\u597d\u4e00\u9663\u5b50\u4e86\u3002\u300d<\/p>\r\n<p>\u300c\u597d\u7684\uff0c\u90a3\u6211\u518d\u7b49\u7b49\u770b\u3002\u300d<\/p>\r\n<p>\u6587\u5349\u4e0d\u662f\u500b\u6703\u8e7a\u8ab2\u7684\u5b69\u5b50\uff0c\u9019\u4e00\u9ede\u9023\u8001\u5e2b\u5f88\u662f\u80af\u5b9a\u3002<\/p>\r\n<p>\u96a8\u8457\u6642\u9593\u6162\u6162\u904e\u53bb\uff0c\u537b\u4f9d\u7136\u4e0d\u898b\u4eba\u5f71\uff0c\u9019\u8b93\u9023\u8001\u5e2b\u6709\u4e9b\u64d4\u5fc3\u8207\u7591\u60d1\uff1a\u300c\u9019\u5b69\u5b50\u7a76\u7adf\u53bb\u54ea\u5152\uff1f\u300d<\/p>\r\n<p>\u6b63\u5728\u72d0\u7591\u7684\u6642\u5019\uff0c\u9023\u8001\u5e2b\u537b\u767c\u73fe\u5927\u9580\u73bb\u7483\u4e0a\u4f3c\u4e4e\u6709\u500b\u4eba\u5f71\u3002\u4ed4\u7d30\u4e00\u77a7\uff0c\u7adf\u662f\u6587\u5349\u3002<\/p>\r\n<p>\u53ea\u662f\uff0c\u9019\u6642\u9580\u4e00\u76f4\u662f\u6253\u958b\u8457\u7684\uff0c\u4f46\u6587\u5349\u537b\u7ad9\u5728\u9580\u5916\u4e0d\u52d5\u3002<\/p>\r\n<p>\u300c\u9019\u5b69\u5b50\u7a76\u7adf\u4f86\u591a\u4e45\u4e86\uff1f\u300d<\/p>\r\n<p>\u9023\u8001\u5e2b\u5fc3\u88e1\u7336\u8c6b\u4e86\u4e00\u4e0b\uff0c\u662f\u5426\u9700\u8981\u8acb\u6587\u5349\u9032\u4f86\u3002<\/p>\r\n<p>\u4f46\u60f3\u60f3\u6309\u7167\u524d\u9762\u5e7e\u6b21\u7684\u7d93\u9a57\uff0c\u7576\u8981\u6587\u5349\u6709\u4e00\u4e9b\u52d5\u4f5c\u53cd\u61c9\uff0c\u7121\u8ad6\u662f\u9032\u4f86\u812b\u978b\u5b50\uff0c\u6216\u5750\u4e0b\u4f86\uff0c\u5979\u7e3d\u662f\u4e0d\u70ba\u6240\u52d5\u3002<\/p>\r\n<p>\u524d\u5e7e\u6b21\uff0c\u53ef\u90fd\u9084\u662f\u73ed\u4e0a\u7684\u5c0f\u670b\u53cb\u966a\u4f34\u524d\u4f86\uff0c\u4e26\u786c\u751f\u751f\u5730\u628a\u6587\u5349\u63a8\u4e86\u9032\u4f86\u3002\u4e0d\u7136\u80fd\u600e\u9ebc\u8fa6\uff0c\u540c\u5b78\u56de\u5230\u6559\u5ba4\uff0c\u53c8\u600e\u9ebc\u5411\u5c0e\u5e2b\u4ea4\u5dee\u3002<\/p>\r\n<p>\u9023\u8001\u5e2b\u5fc3\u88e1\u6399\u624e\u8457\uff0c\u300c\u5982\u679c\uff0c\u9019\u6642\u8981\u6587\u5349\u9032\u4f86\uff0c\u5979\u6c92\u53cd\u61c9\uff0c\u600e\u9ebc\u8fa6\uff1f\u300d<\/p>\r\n<p>\u9019\u4e00\u9ede\uff0c\u4e0d\u5f97\u4e0d\u9867\u616e\u3002\u56e0\u70ba\uff0c\u7576\u4e00\u6b21\u53c8\u4e00\u6b21\u7684\u63d0\u9192\uff0c\u50ac\u4fc3\u8457\u6587\u5349\uff0c\u5979\u6709\u53cd\u61c9\u7684\u6a5f\u7387\u5e7e\u4e4e\u53ea\u67090.001\u3002\u9019\u6642\uff0c\u5982\u679c\u53c8\u591a\u4e0b\u4e86\u4e9b\u6307\u4ee4\uff0c\u6bcf\u8aaa\u4e00\u6b21\uff0c\u6587\u5349\u4e0d\u52d5\u7684\u5931\u6557\u7d93\u9a57\uff0c\u5c31\u6703\u591a\u589e\u52a0\u4e00\u6b21\u3002\u7576\u7136\uff0c\u9019\u5c0d\u65bc\u9023\u8001\u5e2b\u4f86\u8aaa\uff0c\u5fc3\u88e1\u4e5f\u6709\u4e9b\u8ca0\u64d4\u3002\u8aaa\uff0c\u4e0d\u8aaa\uff0c\u5f88\u662f\u5169\u96e3\u3002<\/p>\r\n<p>\u300c\u4f46\u662f\uff0c\u6587\u5349\u4e00\u76f4\u7ad9\u5728\u9580\u53e3\uff0c\u4e5f\u4e0d\u662f\u8fa6\u6cd5\u2026\u2026\u300d<\/p>\r\n<p>\u9023\u8001\u5e2b\u77e5\u9053\u9019\u4ef6\u4e8b\u60c5\uff0c\u81ea\u5df1\u6025\u4e0d\u5f97\uff0c\u6216\u8a31\uff0c\u5f85\u6587\u5349\u56de\u5230\u5bb6\u88e1\uff0c\u518d\u8acb\u5c0e\u5e2b\u8f49\u9054\uff0c\u8b93\u7238\u5abd\u4f86\u8a62\u554f\u5979\uff0c\u9072\u9072\u4e0d\u9032\u4f86\u6664\u8ac7\u5ba4\u7684\u539f\u56e0\uff0c\u5230\u5e95\u662f\u4ec0\u9ebc\u3002\u662f\u5bb3\u6015\u548c\u8f14\u5c0e\u8001\u5e2b\u898b\u9762\uff1f\u4e0d\u60f3\u8981\u4e0a\u9019\u5802\u8ab2\uff1f\u6216\u662f\uff0c\u6587\u5349\u5728\u7dca\u5f35\u4ec0\u9ebc\uff1f\u7126\u616e\u4ec0\u9ebc\uff1f<\/p>\r\n<p>\u300c\u597d\u5427\uff0c\u5c31\u9019\u9ebc\u505a\u4e86\u3002\u300d\u9023\u8001\u5e2b\u6c7a\u5b9a\u9019\u6b21\u6539\u63a1\u53d6\u88ab\u52d5\u7684\u65b9\u5f0f\uff0c\u5148\u4e0d\u4e3b\u52d5\u8981\u6587\u5349\u9032\u5165\u6664\u8ac7\u5ba4\u3002<\/p>\r\n<p>\u4e8b\u5be6\u4e0a\uff0c\u6587\u5349\u4e5f\u77e5\u9053\uff0c\u81ea\u5df1\u5c31\u5728\u88e1\u9762\u7b49\u8457\u5979\u3002\u8d81\u9019\u500b\u6a5f\u6703\uff0c\u4e5f\u8b93\u81ea\u5df1\u9032\u4e00\u6b65\u89c0\u5bdf\u6587\u5349\u7684\u7126\u616e\u53cd\u61c9\uff0c\u4ee5\u4f5c\u70ba\u5f8c\u7e8c\u8f14\u5c0e\u3001\u4ecb\u5165\u7684\u53c3\u8003\u3002<\/p>\r\n<p>\u96a8\u8457\u6642\u9593\u6162\u6162\u904e\u53bb\uff0c\u539f\u672c\u5b89\u975c\u7684\u8d70\u5eca\u4e0a\u958b\u59cb\u6709\u4e09\u4e09\u5169\u5169\u7684\u5c0f\u670b\u53cb\u5f9e\u65c1\u908a\u8d70\u904e\uff0c\u804a\u5929\u7684\u3001\u8aaa\u8a71\u7684\u3001\u73a9\u800d\u7684\u3001\u5b09\u9b27\u7684\u3001\u8ffd\u9010\u7684\u3002<\/p>\r\n<p>\u4f46\u662f\uff0c\u6587\u5349\u4f9d\u7136\u7ad9\u5728\u9580\u53e3\u3002<\/p>\r\n<p>\u4e0d\u904e\uff0c\u9023\u8001\u5e2b\u767c\u73fe\uff0c\u6587\u5349\u51fa\u73fe\u4e86\u4e00\u500b\u5c0f\u52d5\u4f5c\uff0c\u5979\u7684\u982d\u5411\u88e1\u9762\u63a2\u4e86\u4e00\u4e0b\u3002<\/p>\r\n<p>\u9019\u500b\u7d30\u5fae\u7684\u8209\u52d5\uff0c\u5c0d\u9023\u8001\u5e2b\u4f86\u8aaa\uff0c\u537b\u662f\u6587\u5349\u4e00\u9805\u5c0f\u5c0f\u7684\u7a81\u7834\u3002\u96d6\u7136\uff0c\u770b\u4f3c\u5fae\u4e0d\u8db3\u9053\u3002<\/p>\r\n<h2><span style=\"color: #ff9900;\"><strong>\u5c0d\u65bc\u9078\u64c7\u6027\u7dd8\u9ed8\u75c7\u5b69\u5b50\u4f86\u8aaa\uff0c\u8aaa\u8a71\u8207\u4e92\u52d5\u90fd\u662f\u6c89\u91cd\u58d3\u529b<\/strong><\/span><\/h2>\r\n<p>\u5c0d\u65bc\u56b4\u91cd\u7684\u9078\u64c7\u6027\u7dd8\u9ed8\u75c7\u5b69\u5b50\u4f86\u8aaa\uff0c\u5728\u6559\u5ba4\u88e1\uff0c\u9664\u4e86\u5178\u578b\u7684\u4e0d\u958b\u53e3\uff0c\u8207\u8001\u5e2b\u548c\u540c\u5b78\u4e4b\u9593\uff0c\u5e38\u5e38\u6c92\u6709\u4efb\u4f55\u975e\u8a9e\u8a00\u7684\u4e92\u52d5\uff0c\u5c0d\u65bc\u8001\u5e2b\u7684\u6307\u4ee4\u3001\u540c\u5b78\u7684\u9080\u7d04\uff0c\u986f\u5f97\u4e0d\u70ba\u6240\u52d5\u4e4b\u5916\uff0c\u4f60\u9084\u6703\u767c\u73fe\uff0c\u5b69\u5b50\u6574\u500b\u4eba\u5982\u540c\u6728\u982d\u4e00\u822c\uff0c\u7121\u8ad6\u4f60\u548c\u4ed6\u4e4b\u9593\uff0c\u900f\u904e\u8a9e\u8a00\u3001\u975e\u8a9e\u8a00\u7684\u65b9\u5f0f\uff0c\u53bb\u8a62\u554f\u3001\u9080\u8acb\u3001\u5bd2\u6684\u3001\u5206\u4eab\uff0c\u5b69\u5b50\u4f9d\u7136\u5168\u8eab\u50f5\u786c\uff0c\u77d7\u7acb\u5728\u4f60\u7684\u773c\u524d\u3002<\/p>\r\n<p>\u6211\u4e00\u76f4\u601d\u8003\uff0c\u70ba\u4ec0\u9ebc\u9019\u5b69\u5b50\u4e00\u76f4\u7ad9\u5728\u6664\u8ac7\u5ba4\u7684\u9580\u5916\uff0c\u800c\u4e0d\u9858\u610f\u8e0f\u9032\u6664\u8ac7\u5ba4\u3002\u70ba\u4ec0\u9ebc\u5b69\u5b50\u9072\u9072\u4e0d\u52d5\uff1f\u5230\u5e95\u8981\u600e\u9ebc\u505a\uff0c\u624d\u6709\u8fa6\u6cd5\u9a45\u52d5\u5b69\u5b50\u90a3\u6c89\u91cd\u7684\u6b65\u4f10\uff0c\u7de9\u7de9\u5730\u8de8\u9032\u4f86\uff1f<\/p>\r\n<p>\u6709\u4e9b\u5b69\u5b50\u5230\u4e86\u8f14\u5c0e\u6642\u9593\uff0c\u537b\u4e00\u76f4\u6c92\u6709\u62b5\u9054\u3002\u9047\u5230\u9019\u7a2e\u60c5\u6cc1\uff0c\u6211\u6703\u5148\u78ba\u8a8d\u5b69\u5b50\u662f\u5426\u524d\u4f86\u6664\u8ac7\u5ba4\u3002\u5982\u679c\u7b54\u6848\u662f\u80af\u5b9a\u7684\uff0c\u90a3\u6211\u6703\u8a66\u8457\u63a2\u7a76\u5b69\u5b50\u59cd\u59cd\u4f86\u9072\uff0c\u6240\u8981\u50b3\u905e\u7684\u8a0a\u606f\u3002<\/p>\r\n<p>\u53ef\u60f3\u800c\u77e5\uff0c\u524d\u4f86\u6664\u8ac7\u9019\u4ef6\u4e8b\uff0c\u5c0d\u5b69\u5b50\u4f86\u8aaa\uff0c\u5c31\u662f\u4e00\u7a2e\u6c89\u91cd\u7684\u58d3\u529b\uff0c\u751a\u81f3\u65bc\u4e0d\u4e9e\u65bc\u5728\u6559\u5ba4\u88e1\u958b\u53e3\u8aaa\u8a71\u9019\u4ef6\u4e8b\u3002<\/p>\r\n<p>\u7576\u5b69\u5b50\u7ad9\u5728\u9580\u5916\uff0c\u4e0d\u9858\u610f\u8de8\u9032\u4f86\u9032\u884c\u8f14\u5c0e\u8aee\u5546\u3002\u9019\u6642\uff0c\u96e3\u514d\u8b93\u8001\u5e2b\u611f\u5230\u7126\u8e81\u3001\u82e6\u60f1\uff0c\u4e0d\u77e5\u8a72\u5982\u4f55\u662f\u597d\u3002<\/p>\r\n<p>\u6211\u53ef\u4ee5\u78ba\u5b9a\u9019\u6a23\u7684\u5b69\u5b50\uff0c\u5728\u77ed\u6642\u9593\u5167\uff0c\u662f\u4e0d\u5bb9\u6613\u4e3b\u52d5\u8d70\u9032\u9019\u500b\u6559\u5ba4\u7684\u3002\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u9ebc\uff0c\u5c31\u6253\u7834\u6163\u4f8b\uff0c\u4f60\u53ef\u4ee5\u9078\u64c7\u5728\u8d70\u5eca\u4e0a\u548c\u4ed6\u4e92\u52d5\uff0c\u5982\u679c\u6c92\u6709\u5176\u4ed6\u5e72\u64fe\u5b58\u5728\u7684\u8a71\u3002<\/p>\r\n<p>\u7576\u5b69\u5b50\u5728\u6664\u8ac7\u904e\u7a0b\u4e2d\u4e0d\u8aaa\u8a71\uff0c\u6211\u901a\u5e38\u9078\u64c7\u7684\u65b9\u5f0f\uff0c\u662f\u8a66\u8457\u5e6b\u5b69\u5b50\u8aaa\u51fa\u4ed6\u5fc3\u88e1\u9762\uff0c\u6216\u8a31\uff0c\u90a3\u4e9b\u662f\u9023\u4ed6\u81ea\u5df1\u90fd\u4e0d\u662f\u5f88\u6e05\u695a\u7684\u60f3\u6cd5\u8207\u611f\u53d7\u3002\u8a66\u8457\u53cd\u6620\u5b69\u5b50\u7684\u5fc3\u601d\uff0c\u9019\u4efd\u540c\u7406\uff0c\u770b\u4f3c\u597d\u505a\u3001\u597d\u8aaa\uff0c\u4f46\u662f\u4e26\u4e0d\u5bb9\u6613\u3002<\/p>\r\n<p>\u5728\u5e6b\u5b69\u5b50\u8aaa\u7684\u904e\u7a0b\u4e2d\uff0c\u4e5f\u8a66\u8457\u8b93\u4ed6\u7df4\u7fd2\u89ba\u5bdf\uff0c\u8b93\u81ea\u5df1\u7522\u751f\u83ab\u540d\u7684\u7126\u616e\uff0c\u904e\u5ea6\u7684\u6050\u614c\u3001\u5bb3\u6015\u3001\u714e\u71ac\u8207\u56f0\u64fe\uff0c\u9032\u800c\u9020\u6210\u81ea\u5df1\u5728\u73ed\u4e0a\u7121\u6cd5\u9806\u5229\u958b\u53e3\u8aaa\u8a71\uff0c\u53ef\u80fd\u5b58\u5728\u7684\u4e0d\u5408\u7406\u60f3\u6cd5\u3002<\/p>\r\n<p>\u5728\u540c\u7406\u53cd\u6620\u7684\u904e\u7a0b\u4e2d\uff0c\u8acb\u9069\u6642\u7559\u610f\u5b69\u5b50\u7684\u53cd\u61c9\uff0c\u4ee5\u6c7a\u5b9a\u773c\u524d\u9019\u500b\u5b69\u5b50\uff0c\u76ee\u524d\u80fd\u5920\u63a5\u53d7\u7684\u7a0b\u5ea6\uff0c\u4ee5\u53ca\u6211\u5011\u8a71\u984c\u8981\u89f8\u53ca\u7684\u6df1\u5ea6\u8207\u5ee3\u5ea6\u3002\u7562\u7adf\uff0c<strong>\u5c0d\u65bc\u56b4\u91cd\u7684\u9078\u7dd8\u5152\u4f86\u8aaa\uff0c\u5148\u8a66\u8457\u8b93\u4ed6\u611f\u53d7\u5230\u4e92\u52d5\u7684\u81ea\u5728\uff0c\u6700\u662f\u512a\u5148\u3002<\/strong><\/p>\r\n<p><strong><!-- pagebreak --><\/strong><\/p>\r\n<h2><span style=\"color: #ff9900;\"><strong>\u7d66\u5927\u4eba\u7684\u5efa\u8b70\uff1a\u5148\u4e0d\u8981\u6c42\u5b69\u5b50\u7acb\u523b\u958b\u53e3\u8aaa\u8a71<\/strong><\/span><\/h2>\r\n<p>\u5c0d\u65bc\u4e0d\u540c\u7684\u7dd8\u9ed8\u75c7\u5b69\u5b50\uff0c\u5728\u76ee\u6a19\u8a2d\u5b9a\u4e0a\uff0c\u5b8c\u5168\u4e0d\u4e00\u6a23\u3002<\/p>\r\n<p>\u6709\u6642\uff0c\u6211\u5011\u592a\u904e\u65bc\u5f37\u8abf\u975c\u975c\u7684\u7b49\u5019\u3001\u88ab\u52d5\u7684\u7b49\u5019\uff0c\u9019\u4e9b\u592a\u904e\u65bc\u6d88\u6975\u7684\u4f5c\u70ba\uff0c\u53cd\u800c\u5bb9\u6613\u8b93\u9019\u6a23\u7684\u5b69\u5b50\u7e7c\u7e8c\u505c\u7559\u5728\u7dd8\u9ed8\u72c0\u614b\u3002<\/p>\r\n<p><strong>\u56b4\u91cd\u9078\u7dd8\u5152\u7684\u95dc\u4fc2\u5efa\u7acb\u4e26\u4e0d\u5bb9\u6613\uff0c\u81f3\u5c11\u77ed\u671f\u3001\u4e2d\u671f\u7684\u76ee\u6a19\uff0c\u6211\u4e26\u4e0d\u6703\u8a2d\u5b9a\u5728\u8981\u8b93\u4ed6\u958b\u53e3\u8aaa\u8a71\uff0c\u800c\u662f\u8b93\u4ed6\u80fd\u5920\u81ea\u5728\u5730\u8207\u8f14\u5c0e\u8001\u5e2b\u3001\u5fc3\u7406\u5e2b\u7b49\u898b\u9762\u3002<\/strong><\/p>\r\n<p>\u53ea\u8981\u898b\u9762\uff0c\u4e0d\u9700\u8981\u958b\u53e3\u3002\u96d6\u7136\uff0c\u9019\u662f\u770b\u4f3c\u5fae\u4e0d\u8db3\u9053\u7684\u5c0f\u4e8b\uff0c\u4f46\u5c0d\u65bc\u56b4\u91cd\u7684\u9078\u7dd8\u5152\u4f86\u8aaa\uff0c\u537b\u662f\u9700\u8981\u52c7\u6c23\u9762\u5c0d\u7684\u5927\u4e8b\u3002<\/p>\r\n<p>\u9762\u5c0d\u56b4\u91cd\u9078\u64c7\u6027\u7dd8\u9ed8\u75c7\u7684\u5b69\u5b50\uff0c\u8001\u5e2b\u8a72\u600e\u9ebc\u8fa6\uff1f<\/p>\r\n<p>\u5728\u9019\u7576\u4e0b\uff0c\u6211\u6703\u5efa\u8b70\u628a\u9700\u8981\u8a9e\u8a00\u7684\u4e92\u52d5\u6d3b\u52d5\uff0c\u66ab\u6642\u64f1\u7f6e\u5728\u4e00\u65c1\uff0c\u5148\u4e0d\u53bb\u9032\u884c\u592a\u591a\u554f\u984c\u7684\u8a62\u554f\u3002<\/p>\r\n<p>\u9019\u6642\uff0c\u8001\u5e2b\u7684\u6307\u4ee4\uff0c\u4e3b\u8981\u662f\u4ee5\u5168\u73ed\u6027\u7684\u6d3b\u52d5\u70ba\u4e3b\u3002\u4f8b\u5982\uff0c\u8b93\u5168\u73ed\u540c\u5b78\u958b\u59cb\u6574\u7406\u684c\u6905\uff0c\u958b\u59cb\u9032\u884c\u6253\u6383\u3002\u5728\u9019\u6253\u6383\u904e\u7a0b\u4e2d\uff0c\u8001\u5e2b\u6703\u958b\u59cb\u53bb\u89c0\u5bdf\u773c\u524d\u7684\u9078\u64c7\u6027\u7dd8\u9ed8\u5b69\u5b50\u662f\u5426\u4f9d\u7136\u5750\u5728\u4f4d\u7f6e\u4e0a\uff0c\u6216\u8005\u662f\u7de9\u6162\u79fb\u52d5\uff0c\u6309\u7167\u8001\u5e2b\u7684\u898f\u5b9a\uff0c\u91dd\u5c0d\u81ea\u5df1\u7576\u5929\u8a72\u8ca0\u8cac\u7684\u6253\u6383\u5340\u57df\uff0c\u958b\u59cb\u9032\u884c\u6253\u6383\u5de5\u4f5c\u3002\u5728\u6253\u6383\u7684\u904e\u7a0b\u4e2d\uff0c\u5b69\u5b50\u7684\u52d5\u4f5c\u662f\u5426\u4fd0\u843d\uff0c\u9084\u662f\u53ea\u662f\u62ff\u8457\u6383\u628a\uff0c\u773c\u775b\u671b\u8457\u5730\u677f\uff0c\u800c\u770b\u4e0d\u51fa\u6709\u4efb\u4f55\u5728\u9032\u884c\u7684\u52d5\u4f5c\u3002<\/p>\r\n<p>\u7576\u6253\u6383\u6642\u9593\u5df2\u5230\uff0c\u7121\u8ad6\u9078\u64c7\u6027\u7dd8\u9ed8\u5b69\u5b50\u76ee\u524d\u7684\u6253\u6383\u9032\u5ea6\u505a\u5230\u54ea\u88e1\uff0c\u8001\u5e2b\u4f9d\u7136\u5c0d\u5168\u73ed\u4e0b\u9054\u6307\u4ee4\uff0c\u800c\u7576\u6536\u62fe\u4e86\u6253\u6383\u7528\u5177\uff0c\u6e96\u5099\u9032\u884c\u4e0b\u4e00\u500b\u6d3b\u52d5\uff0c\u9019\u6642\uff0c\u518d\u4ed4\u7d30\u5730\u89c0\u5bdf\u773c\u524d\u7684\u9078\u64c7\u6027\u7dd8\u9ed8\u5b69\u5b50\uff0c\u5728\u79fb\u52d5\u7684\u901f\u5ea6\u4e0a\uff0c\u4ee5\u53ca\u52d5\u4f5c\u7684\u64fa\u52d5\u4e0a\uff0c\u662f\u5426\u6f38\u6f38\u5730\u660e\u986f\u3002<\/p>\r\n<p>\u5728\u904e\u7a0b\u4e2d\uff0c\u6d3b\u52d5\u7684\u9078\u64c7\uff0c\u4e3b\u8981\u4ee5\u9019\u500b\u5b69\u5b50\u5728\u6559\u5ba4\u88e1\uff0c\u5728\u4ed6\u7684\u80fd\u529b\u7bc4\u570d\u5167\uff0c\u4ed6\u53ef\u4ee5\u61c9\u4ed8\u4ee5\u53ca\u8655\u7406\u7684\u4e8b\u7269\u70ba\u4e3b\uff0c\u4ee5\u6e1b\u5c11\u9019\u5b69\u5b50\u5c0d\u65bc\u88ab\u8981\u6c42\u4e0d\u9806\u624b\u3001\u4e0d\u64c5\u9577\uff0c\u6216\u662f\u56e0\u4e0d\u7406\u89e3\u7684\u4efb\u52d9\u800c\u7522\u751f\u58d3\u529b\uff0c\u9020\u6210\u4ed6\u7684\u7dd8\u9ed8\u884c\u70ba\u66f4\u52a0\u60e1\u5316\u3002<\/p>\r\n<p>\u8001\u5e2b\u4e5f\u8a31\u6703\u554f\uff1a\u9019\u6a23\u7684\u505a\u6cd5\u5230\u5e95\u8981\u9032\u884c\u5230\u4ec0\u9ebc\u7a0b\u5ea6\uff1f\u9019\u7576\u4e2d\uff0c\u4e3b\u8981\u662f\u8003\u91cf\u5b69\u5b50\u73fe\u968e\u6bb5\u662f\u5426\u6709\u9054\u5230\u6211\u5011\u6240\u8a2d\u5b9a\u7684\u76ee\u6a19\u3002\u4f8b\u5982\uff0c\u7576\u8001\u5e2b\u4e00\u4e0b\u9054\uff1a\u300c\u6253\u6383\u6642\u9593\u5230\u4e86\uff0c\u5404\u4f4d\u540c\u5b78\u958b\u59cb\u62ff\u6383\u9664\u7528\u5177\u3002\u300d\u9019\u6642\uff0c\u6211\u5011\u53ef\u4ee5\u8a66\u8457\u4f86\u8ffd\u8e64\u3001\u4e86\u89e3\uff0c\u9078\u64c7\u6027\u7dd8\u9ed8\u5b69\u5b50\uff0c\u4ed6\u662f\u5426\u53ef\u4ee5\u9806\u5229\u5b8c\u6210\u9019\u4ef6\u4e8b\u3002<\/p>\r\n<h2><span style=\"color: #ff9900;\"><strong>\u71df\u9020\u8f15\u9b06\u6c1b\u570d\uff0c\u6e1b\u8f15\u7126\u616e<\/strong><\/span><\/h2>\r\n<p>\u7576\u7136\uff0c\u53ef\u4ee5\u78ba\u5b9a\u7684\u662f\uff0c\u5927\u90e8\u5206\u7684\u5b69\u5b50\u90fd\u80fd\u7406\u89e3\u8001\u5e2b\u7684\u6307\u4ee4\uff0c\u4e0d\u904e\uff0c\u5b69\u5b50\u5b8c\u6210\u9019\u6a23\u7684\u4e00\u9805\u6307\u4ee4\uff0c\u4ed6\u9700\u8981\u82b1\u8cbb\u591a\u5c11\u6642\u9593\u3002<\/p>\r\n<p>\u6709\u4e9b\u56b4\u91cd\u9078\u7dd8\u7684\u5b69\u5b50\uff0c\u6703\u5728\u5176\u4ed6\u540c\u5b78\u90fd\u5b8c\u6210\u6253\u6383\u5de5\u4f5c\u5f8c\uff0c\u4ed6\u4f9d\u7136\u7ad9\u5728\u6253\u6383\u5de5\u5177\u7bb1\u524d\uff0c\u5b8c\u5168\u4e0d\u70ba\u6240\u52d5\u3002<\/p>\r\n<p>\u9019\u6642\uff0c\u6211\u5011\u53ef\u80fd\u5c31\u8981\u8003\u91cf\uff0c\u662f\u5426\u9700\u8981\u5c07\u76ee\u6a19\u518d\u5f80\u524d\u4fee\u6b63\uff0c\u4ee5\u964d\u4f4e\u96e3\u5ea6\u3002\u4e3b\u8981\u7528\u610f\u662f\u907f\u514d\u7576\u5b69\u5b50\u5446\u7acb\u5728\u6253\u6383\u7528\u5177\u524d\uff0c\u5b8c\u5168\u6c92\u6709\u53cd\u61c9\uff0c\u9019\u6642\uff0c\u66f4\u5bb9\u6613\u52a0\u6df1\u5176\u4ed6\u540c\u5b78\u5c0d\u4ed6\u7684\u523b\u677f\u5370\u8c61\uff0c\u4e5f\u5bb9\u6613\u9020\u6210\u7576\u4e8b\u4eba\u66f4\u52a0\u7126\u616e\u3002<\/p>\r\n<p>\u6240\u4ee5\u8acb\u66f4\u6539\u6253\u6383\u7684\u5167\u5bb9\uff0c\u4e5f\u8a31\uff0c\u8b93\u9078\u64c7\u6027\u7dd8\u9ed8\u75c7\u5b69\u5b50\u8ca0\u8cac\u64e6\u62ed\u4ed6\u81ea\u5df1\u7684\u684c\u5b50\u3002\u4f46\u70ba\u4e86\u907f\u514d\u5176\u4ed6\u540c\u5b78\u8a8d\u70ba\u8001\u5e2b\u904e\u5ea6\u504f\u8892\u9078\u64c7\u6027\u7dd8\u9ed8\u75c7\u5b69\u5b50\uff0c\u300c\u70ba\u4ec0\u9ebc\u5176\u4ed6\u7684\u540c\u5b78\u8981\u505a\u5de5\u4f5c\u91cf\u6bd4\u8f03\u5927\u7684\u6253\u6383\uff0c\u800c\u4ed6\u537b\u53ea\u8981\u64e6\u62ed\u81ea\u5df1\u7684\u684c\u5b50\uff1f\u300d\u6240\u4ee5\u8001\u5e2b\u540c\u6642\u4e5f\u53ef\u4ee5\u6307\u5b9a\u5176\u4ed6\u5e7e\u4f4d\u540c\u5b78\uff0c\u505a\u985e\u4f3c\u7684\u6253\u6383\u5167\u5bb9\uff0c\u4ee5\u964d\u4f4e\u5176\u4ed6\u540c\u5b78\u5c0d\u7576\u4e8b\u4eba\u7684\u904e\u5ea6\u6ce8\u610f\u3002<\/p>\r\n<p>\u5728\u9019\u88e1\uff0c\u6253\u6383\u7684\u5de5\u4f5c\uff0c\u53ea\u662f\u5176\u4e2d\u4e00\u500b\u8209\u4f8b\uff0c\u8001\u5e2b\u5011\u53ef\u4ee5\u91dd\u5c0d\u73ed\u4e0a\u5b69\u5b50\u7684\u72c0\u6cc1\uff0c\u5b89\u6392\u9069\u5408\u5728\u5404\u81ea\u73ed\u4e0a\uff0c\u4ee5\u53ca\u9069\u5408\u7576\u4e8b\u4eba\u7684\u6d3b\u52d5\u3002<\/p>\r\n<p>\u7531\u65bc\u56b4\u91cd\u6027\u7684\u9078\u64c7\u6027\u7dd8\u9ed8\u75c7\u5b69\u5b50\uff0c\u5728\u793e\u4ea4\u4e92\u52d5\u4e0a\uff0c\u5f80\u5f80\u5bb9\u6613\u7522\u751f\u6975\u5ea6\u7684\u5bb3\u6015\u3001\u6050\u61fc\uff0c\u4f7f\u5f97\u4ed6\u5728\u6574\u500b\u6559\u5ba4\u88e1\u7684\u53cd\u61c9\uff0c\u6703\u8b93\u5468\u570d\u7684\u4eba\u660e\u986f\u611f\u53d7\u5230\u4e0d\u5c0d\u52c1\uff0c\u9019\u6642\uff0c\u8001\u5e2b\u82e5\u80fd\u71df\u9020\u6109\u6085\u3001\u8f15\u9b06\u3001\u6b61\u7b11\u3001\u81ea\u5728\u958b\u5fc3\u7684\u60c5\u5883\uff0c\u5c0d\u65bc\u9078\u64c7\u6027\u7dd8\u9ed8\u75c7\u5b69\u5b50\u4f86\u8b1b\uff0c\u6703\u662f\u4e00\u7a2e\u975e\u5e38\u5927\u7684\u5e6b\u52a9\u3002<\/p>\r\n<p>\u9019\u6a23\u7684\u6c1b\u570d\uff0c\u6709\u52a9\u65bc\u8b93\u5b69\u5b50\u66f4\u52a0\u5730\u8f15\u9b06\u3001\u81ea\u5728\uff0c\u4e5f\u80fd\u8b93\u81ea\u5df1\u6162\u6162\u4e0d\u518d\u808c\u8089\u5982\u6b64\u7dca\u7e43\u3002<\/p>\r\n<p>\u9019\u985e\u578b\u5b69\u5b50\u7684\u6539\u8b8a\u9806\u5e8f\uff0c\u5f80\u5f80\u5148\u5f9e\u975e\u8a9e\u8a00\u7684\u80a2\u9ad4\u52d5\u4f5c\uff0c\u81c9\u90e8\u8868\u60c5\u51fa\u73fe\u7d30\u5fae\u7684\u6539\u8b8a\uff0c\u518d\u9010\u6f38\u589e\u52a0\u5c0d\u65bc\u8001\u5e2b\u6307\u4ee4\u7684\u914d\u5408\u5ea6\u8207\u5b8c\u6210\u5ea6\u3002\u540c\u6642\uff0c\u5728\u904e\u7a0b\u4e2d\uff0c\u4e5f\u9010\u6f38\u589e\u52a0\u56de\u61c9\u7684\u901f\u5ea6\uff0c\u4e0d\u81f3\u65bc\u50cf\u4ee5\u5f80\u51fa\u73fe\u5df2\u8b80\u4e0d\u56de\u7684\u72c0\u614b\u3002<\/p>\r\n<p>\u4e0d\u904e\u5230\u9019\u88e1\uff0c\u8ddd\u96e2\u5b69\u5b50\u662f\u5426\u80fd\u5920\u6709\u8072\u97f3\u51fa\u73fe\uff0c\u8aaa\u771f\u7684\uff0c\u4f9d\u7136\u9084\u662f\u6709\u4e00\u6bb5\u975e\u5e38\u5927\u7684\u8ddd\u96e2\u3002<\/p>\r\n<p><span style=\"color: #808080;\"><strong>\u4f5c\u8005\u7c21\u4ecb<\/strong><strong>\u2502<\/strong><strong>\u738b\u610f\u4e2d<\/strong><\/span><\/p>\r\n<p><span style=\"color: #808080;\">\u738b\u610f\u4e2d\u5fc3\u7406\u6cbb\u7642\u6240\u6240\u9577\u3001\u81e8\u5e8a\u5fc3\u7406\u5e2b\u3001\u6559\u80b2\u90e8\u90e8\u5b9a\u8b1b\u5e2b\u3001\u300a\u89aa\u5b50\u5929\u4e0b\u300b\u56b4\u9078\u90e8\u843d\u5ba2\u3002\u65bc\u5b9c\u862d\u6210\u7acb\u5b9c\u862d\u7e23\u7b2c\u4e00\u6240\u7d93\u885b\u751f\u4e3b\u7ba1\u6a5f\u95dc\u5408\u6cd5\u7acb\u6848\u4e4b\u5fc3\u7406\u6cbb\u7642\u6240\uff0c\u662f\u8a31\u591a\u5bb6\u9577\u8207\u8001\u5e2b\u5fc3\u4e2d\u6975\u70ba\u4fe1\u8cf4\u7684\u5fc3\u7406\u5e2b\u3002\u65bc\u7db2\u8def\u4e0a\uff0c\u7d93\u71df\u300c\u738b\u610f\u4e2d\u90e8\u843d\u683c\u300d\uff0c\u5206\u4eab\u65e9\u671f\u7642\u80b2\u53ca\u5152\u7ae5\u9752\u5c11\u5e74\u5fc3\u7406\u885b\u751f\u7b49\u8cc7\u8a0a\u3002<\/span><\/p>\r\n<p><strong>\uff0a\u672c\u6587\u6458\u81ea\u5bf6\u74f6\u6587\u5316\u51fa\u7248<\/strong><a href=\"https:\/\/www.books.com.tw\/products\/0010841889\"><strong>\u300a<\/strong><strong>\u9078\u64c7\u6027\u7dd8\u9ed8\u75c7\uff1a\u4e0d\u8aaa\u8a71\u7684\u5b69\u5b50<\/strong><strong>\u300b<\/strong><\/a><strong>\uff0c\u672a\u7d93\u540c\u610f\u8acb\u52ff\u8f49\u8f09<\/strong><\/p>\r\n<p><strong><img src=\"https:\/\/cp.cw1.tw\/files\/md5\/bd\/ed\/bded0a1cc286964b71233309797d4803-1246249.jpg\" width=\"180\" height=\"auto\" \/><\/strong><\/p>"}}}
